import React, { useState } from 'react'
import axios from 'axios'

function Card() {
    const [pokemon, setPokemon] = useState({name:"", photo:""})
    const getPoke = async (event) => {
        const result = await axios.get("https://pokeapi.co/api/v2/pokemon/" + event.target.value)
        console.log(result.data)
        const data = {
            name:result.data.forms[0].name,
            photo:result.data.sprites.front_shiny,
            moves:result.data.moves
        }
        setPokemon(data)
    }
    return (
        <div>
            <div>
                <img src={pokemon.photo} alt='no'></img>
            </div>
            <div>
                <div>{pokemon.name}</div>

            </div>
            <button onClick={getPoke} value={Math.floor(Math.random() * 100)}>Open the Pack</button>
        </div>
    )
}

export default Card
